CREATE TABLE inflation_rate (
 	id BIGINT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
    version BIGINT NOT NULL DEFAULT 0,

    year SMALLINT NOT NULL CHECK (year >= 1800 AND year <= 3000),
    month SMALLINT NOT NULL CHECK (month >= 1 AND month <= 12),
    inflation_type VARCHAR(50) NOT NULL
    CHECK (inflation_type IN (
        'THIS_YEAR_VS_LAST_YEAR',
        'THIS_MONTH_VS_PREVIOUS_YEARS_MONTH',
        'THIS_MONTH_VS_PREVIOUS_MONTH')
    ),

    inflation_percent REAL NOT NULL,

    UNIQUE (year, month, inflation_type)
);


